#!/bin/bash# EOS Flash Loan Contract Deployment Script# This script deploys the flash loan contract to EOS testnetset -eecho "Starting EOS Flash Loan Contract Deployment..."# ConfigurationEOS_ENDPOINT="https://jungle3.greymass.com"CONTRACT_NAME="flashloan123"WALLET_NAME="flashloan"PRIVATE_KEY="5KQwrPbwdL6PhXujxW37FSSQZ1JiwsST4cqQzDeyXtP79zkvFD3"# Create wallet if it doesn't existecho "Setting up wallet..."cleos wallet create --to-console || truecleos wallet import --private-key $PRIVATE_KEY# Create accountecho "Creating account..."cleos create account eosio $CONTRACT_NAME EOS6MRyAjQq8ud7hVNYcfnVPJqcVpscN5So8BhtHuGYqET5GDW5CV# Compile contractecho "Compiling EOS contracts..."cd ../contracts/eoseosio-cpp -abigen flashloan.cpp -o flashloan.wasm# Deploy contractecho "Deploying contract..."cleos set contract $CONTRACT_NAME ./ flashloan.wasm flashloan.abi -p $CONTRACT_NAME@active# Initialize liquidity poolecho "Initializing liquidity pool..."cleos push action $CONTRACT_NAME initpool '["eosio.token", "4,EOS", "1000.0000 EOS", 10, 8000]' -p $CONTRACT_NAME@activeecho "EOS Flash Loan Contract deployed successfully!"echo "Contract Address: $CONTRACT_NAME"echo "Transaction Hash: [Transaction hash will be displayed here]"